const isNumberPrime=e=>{if(void 0===e||isNaN(e))return`${e} is not a number.`;if(!Number.isInteger(e))return`${e} is not a prime number because it is not a whole number.`;if(e<0)return`${e} is not a prime number because it is a negative number.`;if(1===e||0===e)return`${e} is not a prime number.`;const r=Math.floor(Math.sqrt(e));for(let i=2;i<=r;i++)if(e%i==0)return`${e} is not a prime number because it is divisible by ${i}`;return`${e} is a prime number! :)`};module.exports={isNumberPrime:isNumberPrime};